import React, {useEffect, useState} from 'react';
import PerfectScrollbar from 'react-perfect-scrollbar';
import { useSelector, useDispatch } from 'react-redux';
import MaterialTable from 'material-table';
import { fetchAllFHealthTips } from '../../actions/healthtips'
import VisibilityIcon from '@material-ui/icons/Visibility';
import DeleteForeverIcon from '@material-ui/icons/DeleteForever';
import Tooltip from '@material-ui/core/Tooltip';
import IconButton from '@material-ui/core/IconButton';
import BorderColorIcon from '@material-ui/icons/BorderColor';
import {
  Menu,
  MenuList,
  MenuButton,
  MenuItem,
} from "@reach/menu-button";
import "@reach/menu-button/styles.css";

const FacilitiesListTable = () => {
  const healthtipsList = useSelector(state => state.healthtips.list);
  const dispatch = useDispatch();
  const [loading, setLoading] = useState(false)
 console.log(healthtipsList)
  useEffect(() => {
      setLoading('true');
      const onSuccess = () => {
        setLoading(false)
      }
      const onError = () => {
        setLoading(false)     
      }
    dispatch(fetchAllFHealthTips(onSuccess,onError))
  }, [dispatch])
  //const questList = questionsList
const viewDetail = (detail) =>{
      console.log(detail)
}
const editDetail = (detail) =>{
  console.log(detail)
}
const deleteDetail = (detail) =>{
  console.log(detail)
}


const Action = (e) =>{

  return (

      <Menu>
          <MenuButton style={{ backgroundColor:"#3F51B5", color:"#fff", border:"2px solid #3F51B5", borderRadius:"4px"}}>
            Action <span aria-hidden>â–¾</span>
          </MenuButton>
          <MenuList style={{hover:"#eee"}}>
            <MenuItem onSelect={() => handlesample(e)}><FaPlusSquare size="15" style={{color: '#000'}}/>{" "}Collect Sample</MenuItem>
            <MenuItem onSelect={() => transfersample(e)}><TiArrowForward size="15" style={{color: '#000'}}/>{" "} Transfer Sample</MenuItem>             
            {e.data.lab_test_order_status===5 ?
               <MenuItem onSelect={() => viewresult(e)}><FaRegEye size="15" style={{color: '#3F51B5'}}/>{" "}View Result</MenuItem>
               :""}  
          </MenuList>
      </Menu>
      )

}
  return (

        <PerfectScrollbar>
          <div >
          <MaterialTable
        title="List of Health Tips"
        columns={[
          { title: " ID", field: "Id",filtering: false },
          {
            title: "Title",
            field: "title",
            filtering: false
          },

          {
            title: "Content",
            field: "content",
            filtering: false
          },
          {
            title: "Type",
            field: "type",
            filtering: false
          },
          {
            title: "Action",
            field: "actions",
            filtering: false,
          },
        ]}
        isLoading={loading}
        data={healthtipsList.map((row) => ({
          Id: row.id,
          title: row.title,          
          content: row.content,
          type: row.healthtiptype===1 ? "General Health Tips" : "Covid 19 ",
          actions: <div> 
                  <Tooltip title="View Detail">
                      <IconButton aria-label="View Detail" onClick={() => viewDetail(row)} >
                      <VisibilityIcon color="primary"/>
                    </IconButton>
                    </Tooltip>
                    <Tooltip title="Edit">
                    <IconButton aria-label="Edit" onClick={() => editDetail(row)}>
                    <BorderColorIcon color="primary"/>
                  </IconButton>
                  </Tooltip>
                    <Tooltip title="Delete">
                    <IconButton aria-label="Delete" onClick={() => deleteDetail(row)}>
                    <DeleteForeverIcon color="primary"/>
                  </IconButton>
                  </Tooltip>
                  </div> 

            }))}
        options={{

          headerStyle: {
            backgroundColor: "#9F9FA5",
            color: "#000",
            margin: "auto"
          },
          filtering: true,          
          exportButton: true,

        }}

      />
          </div>
        </PerfectScrollbar>

  );
};



export default FacilitiesListTable;
