import React, {useEffect, useState} from 'react';
import PerfectScrollbar from 'react-perfect-scrollbar';
import { useSelector, useDispatch } from 'react-redux';
import MaterialTable from 'material-table';
import { fetchAllFHealthTips } from '../../actions/healthtips'
import VisibilityIcon from '@material-ui/icons/Visibility';
import DeleteForeverIcon from '@material-ui/icons/DeleteForever';
import BorderColorIcon from '@material-ui/icons/BorderColor';
import {
  Menu,
  MenuList,
  MenuButton,
  MenuItem,
} from "@reach/menu-button";
import "@reach/menu-button/styles.css";

const FacilitiesListTable = () => {
  const healthtipsList = useSelector(state => state.healthtips.list);
  const dispatch = useDispatch();
  const [loading, setLoading] = useState(false)
 console.log(healthtipsList)
  useEffect(() => {
      setLoading('true');
      const onSuccess = () => {
        setLoading(false)
      }
      const onError = () => {
        setLoading(false)     
      }
    dispatch(fetchAllFHealthTips(onSuccess,onError))
  }, [dispatch])
  //const questList = questionsList
const viewDetail = (detail) =>{
      console.log(detail)
}
const editDetail = (detail) =>{
  console.log(detail)
}
const deleteDetail = (detail) =>{
  console.log(detail)
}


const actionButtons = (e) =>{

  return (

      <Menu>
          <MenuButton style={{ backgroundColor:"#3F51B5", color:"#fff", border:"2px solid #3F51B5", borderRadius:"4px"}}>
            Action <span aria-hidden>â–¾</span>
          </MenuButton>
          <MenuList style={{hover:"#eee"}}>
            <MenuItem onSelect={() => viewDetail(e)}><VisibilityIcon color="primary"/>{" "}View Detaisl</MenuItem>
            <MenuItem onSelect={() => editDetail(e)}><BorderColorIcon color="primary"/>{" "} Transfer Sample</MenuItem>             

            <MenuItem onSelect={() => deleteDetail(e)}><DeleteForeverIcon color="primary"/>{" "}View Result</MenuItem>

          </MenuList>
      </Menu>
      )

}
  return (

        <PerfectScrollbar>
          <div >
          <MaterialTable
        title="List of Health Tips"
        columns={[
          { title: " ID", field: "Id",filtering: false },
          {
            title: "Title",
            field: "title",
            filtering: false
          },

          {
            title: "Content",
            field: "content",
            filtering: false
          },
          {
            title: "Type",
            field: "type",
            filtering: false
          },
          {
            title: "Action",
            field: "actions",
            filtering: false,
          },
        ]}
        isLoading={loading}
        data={healthtipsList.map((row) => ({
          Id: row.id,
          title: row.title,          
          content: row.content,
          type: row.healthtiptype===1 ? "General Health Tips" : "Covid 19 ",
          actions: actionButtons(row)

            }))}
        options={{

          headerStyle: {
            backgroundColor: "#9F9FA5",
            color: "#000",
            margin: "auto"
          },
          filtering: true,          
          exportButton: true,

        }}

      />
          </div>
        </PerfectScrollbar>

  );
};



export default FacilitiesListTable;
