import React, {useEffect, useState} from 'react';
import PerfectScrollbar from 'react-perfect-scrollbar';
import { useSelector, useDispatch } from 'react-redux';
import MaterialTable from 'material-table';
import { fetchAllFHealthTips } from '../../actions/healthtips'
import { FaRegEye, FaPencilAlt, FaTrashAlt } from 'react-icons/fa';
import {
  Menu,
  MenuList,
  MenuButton,
  MenuItem,
} from "@reach/menu-button";
import "@reach/menu-button/styles.css";
import ModalDelete from './ActionModal/ModalDelete';
import {  Modal, ModalHeader, ModalBody,
  Button,
  } from 'reactstrap';

const FacilitiesListTable = (props) => {
  const {
    buttonLabel,
    className
  } = props;
  const healthtipsList = useSelector(state => state.healthtips.list);
  const dispatch = useDispatch();
  const [loading, setLoading] = useState(false)
  const [collectmodal, setcollectmodal] = useState([])
  const [modal, setModal] = useState(false) //Modal to View  

  // const [modal2, setModal2] = useState(false)//modal to Edit
  // const togglemodal2 = () => setModal2(!modal2)
  const [modal3, setModal3] = useState(false)//modal to Delete
  const togglemodal3 = () => setModal3(!modal3)
  console.log(healthtipsList)
  useEffect(() => {
      setLoading('true');
      const onSuccess = () => {
        setLoading(false)
      }
      const onError = () => {
        setLoading(false)     
      }
    dispatch(fetchAllFHealthTips(onSuccess,onError))
  }, [dispatch])
  //const questList = questionsList
const viewDetail = (detail) =>{
      console.log(detail)
      setcollectmodal({...collectmodal, ...detail});
}
const editDetail = (detail) =>{
  console.log(detail)
}
const deleteDetail = (detail) =>{
  console.log("am here for delete")
  setModal3(!modal3) 
  setcollectmodal({...collectmodal, ...detail});
}


const actionButtons = (e) =>{

  return (

      <Menu>
          <MenuButton style={{ backgroundColor:"#3F51B5", fontSize:"16px", color:"#fff", border:"2px solid #3F51B5", borderRadius:"4px", width:"80px", heigth:"80px"}}>
            Actions <span aria-hidden>▾</span>
          </MenuButton>
          <MenuList style={{hover:"#eee"}}>
            <MenuItem onSelect={() => viewDetail(e)}><FaRegEye color="primary" size="15px" />{" "}View Detail</MenuItem>
            <MenuItem onSelect={() => editDetail(e)}><FaPencilAlt color="primary" size="15px"/>{" "} Edit</MenuItem>             

            <MenuItem onSelect={() => deleteDetail(e)}><FaTrashAlt color="primary" size="15px"/>{" "}Delete</MenuItem>

          </MenuList>
      </Menu>
      )

}
  return (

        <PerfectScrollbar>
          <div >
          <MaterialTable
        title="List of Health Tips"
        columns={[
          { title: " ID", field: "Id",filtering: false },
          {
            title: "Title",
            field: "title",
            filtering: false
          },

          {
            title: "Content",
            field: "content",
            filtering: false
          },
          {
            title: "Type",
            field: "type",
            filtering: false
          },
          {
            title: "Action",
            field: "actions",
            filtering: false,
          },
        ]}
        isLoading={loading}
        data={healthtipsList.map((row) => ({
          Id: row.id,
          title: row.title,          
          content: row.content,
          type: row.healthtiptype===1 ? "General Health Tips" : "Covid 19 ",
          actions: actionButtons(row)

            }))}
        options={{

          headerStyle: {
            backgroundColor: "#9F9FA5",
            color: "#000",
            margin: "auto"
          },
          filtering: true,          
          exportButton: true,

        }}

      />
      <ModalDelete modal={modal3} toggle={togglemodal3} datasample={collectmodal} />
      <Button color="danger" onClick={toggle}>{buttonLabel}</Button>
      <Modal isOpen={modal} toggle={toggle} className={className}>
        <ModalHeader toggle={toggle}>Modal title</ModalHeader>
        <ModalBody>
          Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
        </ModalBody>

      </Modal>
      </div>
    </PerfectScrollbar>

  );

};




export default FacilitiesListTable;
