import React, {useState, useEffect} from 'react';
import { withStyles } from '@material-ui/core/styles';
import Dialog from '@material-ui/core/Dialog';
import MuiDialogTitle from '@material-ui/core/DialogTitle';
import MuiDialogContent from '@material-ui/core/DialogContent';
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@material-ui/icons/Close';
import Typography from '@material-ui/core/Typography';
import { makeStyles } from '@material-ui/styles';
import { Button } from '@material-ui/core';
import {
    Card,
    CardContent,
    CardActions,
    Divider,
    Grid,
    TextField
  } from '@material-ui/core';
import axios from 'axios';
import {url} from './../../api'
import { ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { Spinner } from 'reactstrap';
import { connect } from 'react-redux';
import {createHealthTips} from './../../actions/healthtips'

const styles = (theme) => ({
  root: {
    margin: 0,
    padding: theme.spacing(2),
  },
  closeButton: {
    position: 'absolute',
    right: theme.spacing(1),
    top: theme.spacing(1),
    color: theme.palette.grey[500],
  },

});

const useStyles = makeStyles(theme => ({
    root: {},
    row: {
      height: '42px',
      display: 'flex',
      alignItems: 'center',
      marginTop: theme.spacing(1)
    },
    spacer: {
      flexGrow: 1
    },

    input: {
        display: 'none',
      },
  }));

const DialogTitle = withStyles(styles)((props) => {
  const { children, classes, onClose, ...other } = props;
  return (
    <MuiDialogTitle disableTypography className={classes.root} {...other}>
      <Typography variant="h6">{children}</Typography>
      {onClose ? (
        <IconButton aria-label="close" className={classes.closeButton} onClick={onClose}>
          <CloseIcon />
        </IconButton>
      ) : null}
    </MuiDialogTitle>
  );
});

const DialogContent = withStyles((theme) => ({
  root: {
    padding: theme.spacing(2),
  },
}))(MuiDialogContent);


const AddHealthTips = (props) =>{
  const classes = useStyles();
  const [open, setOpen] = React.useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };
  const handleClose = () => {
    setOpen(false);
  };

  const [values, setValues] = useState({ "title": "",
                                        "content": "",
                                        "imagepath": "",
                                        "healthtiptype": 1,
                                        "language": 1
                                        });
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(false)
  useEffect(() => {
    const fetchData = async () => {
      const result = await axios(
        url+'healthtiptypes',
      );
      setData(result.data);
    };

    fetchData();
  });
  const handleChange = event => {
    setValues({
      ...values,
      [event.target.name]: event.target.value
    });
    console.log(values)
  };

  const savehealthtip = e => {
    e.preventDefault()
    console.log(values)
    const onSuccess = () => {
        setLoading(false);
        handleClose()       
      }
      const onError = () => {
        setLoading(false); 
        handleClose()       
      }
      props.createHealthTips(values, onSuccess,onError)
  }



  return (
    <div>
        <ToastContainer autoClose={3000} hideProgressBar />
      <div className={classes.row}>
      <span className={classes.spacer} />

        <Button
            color="primary"
            variant="contained"
            onClick={handleClickOpen}
            >
          Add Health Tips
        </Button>
      </div>
      <Dialog onClose={handleClose} aria-labelledby="customized-dialog-title" open={open}
            fullWidth='false'
            maxWidth='sm'
      >
        <DialogTitle id="customized-dialog-title" onClose={handleClose}>
          Add Health Tips
        </DialogTitle>
        <DialogContent dividers>
        <Card>
            <form
                autoComplete="off"
                noValidate
                onSubmit={savehealthtip}
            >

                <CardContent>

                <Grid
                    container
                    spacing={3}
                >
                <Grid
                    item
                    md={12}
                    xs={12}
                    >

                     <TextField
                        fullWidth
                        label="Ttile"
                        id="title"
                        className={classes.textField}
                        helperText="Title"
                        margin="dense"
                        onChange={handleChange}
                        variant="outlined"
                        value={values.title}
                    />
                    </Grid>
                    <Grid
                    item
                    md={12}
                    xs={12}
                    >
                    <TextField
                        fullWidth
                        label="Select Type"
                        margin="dense"
                        name="healthtiptype"
                        onChange={handleChange}
                        required
                        select

                        SelectProps={{ native: true }}
                        value={values.healthtiptype}
                        variant="outlined"
                    >
                        <option > </option>
                        {data.map(option => (
                            <option
                                key={option.id}
                                value={option.id}
                            >
                                {option.name}
                            </option>
                            ))}
                    </TextField>
                    </Grid>
                    <Grid
                    item
                    md={12}
                    xs={12}
                    >
                    <TextField
                        fullWidth
                        helperText="Please enter the health tips here "
                        margin="dense"
                        name="content"
                        onChange={handleChange}
                        required
                        id="filled-textarea"
                        label="Health Tip Content "
                        placeholder="Please enter content here "
                        multiline
                        variant="filled"
                        value={values.content}
                    />
                    </Grid>
                    <Grid
                    item
                    md={6}
                    xs={12}
                    >
                     <input
                            accept="image/*"
                            className={classes.input}
                            id="imagepath"
                            multiple
                            type="file"
                            onChange={handleChange}
                            value={values.imagepath}
                        />
                        <label htmlFor="contained-button-file">
                            <Button variant="contained" color="secondary" component="span">
                            Upload
                            </Button>
                        </label>
                    </Grid>

                </Grid>
                </CardContent>
                <Divider />
                <CardActions>
                {loading ? <Spinner /> : ""} <br/>
                    <Button
                        type="submit"
                        color="primary"
                        variant="contained"
                    >
                        Save
                    </Button>
                </CardActions>
            </form>
         </Card>
        </DialogContent>

      </Dialog>
    </div>
  );
}

export default connect(null, { createHealthTips })(AddHealthTips);
