        <PerfectScrollbar>
          <div >
          <MaterialTable
        title="List of Health Tips"
        columns={[
          { title: " ID", field: "Id",filtering: false },
          {
            title: "Title",
            field: "title",
            filtering: false
          },

          {
            title: "Content",
            field: "content",
            filtering: false
          },
          {
            title: "Type",
            field: "type",
            filtering: false
          },
          {
            title: "Action",
            field: "actions",
            filtering: false,
          },
        ]}
        isLoading={loading}
        data={healthtipsList.map((row) => ({
          Id: row.id,
          title: row.title,          
          content: row.content,
          type: row.healthtiptype===1 ? "General Health Tips" : "Covid 19 ",
          actions: <div> 
                  <Tooltip title="View Detail">
                      <IconButton aria-label="View Detail" onClick={() => viewDetail(row)} >
                      <VisibilityIcon color="primary"/>
                    </IconButton>
                    </Tooltip>
                    <Tooltip title="Edit">
                    <IconButton aria-label="Edit" onClick={() => editDetail(row)}>
                    <BorderColorIcon color="primary"/>
                  </IconButton>
                  </Tooltip>
                    <Tooltip title="Delete">
                    <IconButton aria-label="Delete" onClick={() => deleteDetail(row)}>
                    <DeleteForeverIcon color="primary"/>
                  </IconButton>
                  </Tooltip>
                  </div> 

            }))}
        options={{

          headerStyle: {
            backgroundColor: "#9F9FA5",
            color: "#000",
            margin: "auto"
          },
          filtering: true,          
          exportButton: true,

        }}

      />
          </div>
        </PerfectScrollbar>

  );
};



export default FacilitiesListTable;
